using Apricot.Shared.Extensions;
using Apricot.Shared.Models;
using Apricot.Shared.Models.ViewModels;
using System;
using Apricot.WebServices.Models.Plant;

namespace Apricot.Shared.SampleData
{
    /// <summary>
    ///     Fake view model for the "Plant information" view.
    /// </summary>
    [CLSCompliant(false)]
    public class PlantSampleData
    {
        #region Constants.

        /// <summary>
        ///     Fake photos in Base64 format.
        /// </summary>
        private const string FakePhotoBase64Data =
            "";

        #endregion Constants.

        #region Properties.

        /// <summary>
        ///     Gets or sets a model.
        /// </summary>
        public PlantModel Model { get; set; }

        #endregion Properties.

        #region Constructors.

        /// <summary>
        ///     Constructor.
        /// </summary>
        public PlantSampleData()
        {
            // Initialize properties.
            Model = new PlantModel
            {
                Name = "Turpis interdum",
                Identifier = 0,
                Details = new PlantDetailsModel
                {
                    Variety = 0
                },
                Measures = new []
                {
                    new MeasureServiceModel{ Date = "1432659441", Temperature = 21.8},
                    new MeasureServiceModel{ Date = "1432659450", Temperature = 28.1},
                    new MeasureServiceModel{ Date = "1432659450", Temperature = 22.5},
                    new MeasureServiceModel{ Date = "1432659424", Temperature = 24.9}
                },
                LatestMeasure = new MeasureServiceModel
                {
                    Temperature = 26.45,
                    Date = "123456789",
                    Humidity = 26.945,
                    Luminosity = 436.54
                },
                Alert =
                {
                    Humidity = true,
                    Temperature = true
                },
                RemainingTime =
                {
                    LeftTime = 14204
                }
            };

            _InitializeFakePhotos();
        }

        #endregion Constructors.

        #region Methods.

        /// <summary>
        ///     Initialize fake photos.
        /// </summary>
        private async void _InitializeFakePhotos()
        {
            for (var i = 0; i < 9; i++)
            {
                var photo = await BitmapImageExtensions.FromBase64(FakePhotoBase64Data);
                Model.Details.Photos.Add(photo);
            }
        }

        #endregion Methods.
    }
}